mapper.select:
select user_id, pdt_id, pdt_qty
from CART_TB
where user_id = #{user_id};

mapper.update:
update CART_TB
set pdt_qty = pdt_qty + 1
where user_id = #{user_id}
and pdt_id = #{pdt_id};

update CART_TB
set pdt_qty = pdt_qty - 1
where user_id = #{user_id}
  and pdt_id = #{pdt_id};

mapper.delete:
delete
from CART_TB
where user_id = #{user_id}
and pdt_id = #{pdt_id};
