mapper.select:
select user_id, pdt_id, pdt_qty
from CART_TB
where user_id = #{user_id}
and pdt_id = #{pdt_id};

select user_id, CT.pdt_id, pdt_qty, sel_price, title, stock
from CART_TB CT
join PRODUCT_TB PT
on CT.pdt_id = PT.pdt_id
where user_id = #{user_id}
order by CT.in_date DESC;

mapper.update:
update CART_TB
set pdt_qty = #{pdt_qty}
where user_id = #{user_id}
  and pdt_id = #{pdt_id};

mapper.delete:
delete
from CART_TB
where user_id = #{user_id}
and pdt_id = #{pdt_id};
